var mongoose = require('backend/libs/mongoose');
var async = require('async');
var util = require('util');

Schema = mongoose.Schema;

var schema = new Schema({
  title: {
    type: String,
    required: true
  },
  posTop: {
    type: String,
    required: true
  },
  posLeft: {
    type: String,
    required: true
  },
  count : {
    type: String,
    required: true
  }
});


exports.Table = mongoose.model('Table', schema);

